package com.atguigu.yygh.common.coinfig;import com.baomidou.mybatisplus.core.handlers.MetaObjectHandler;import com.baomidou.mybatisplus.extension.plugins.MybatisPlusInterceptor;import com.baomidou.mybatisplus.extension.plugins.inner.PaginationInnerInterceptor;import org.apache.ibatis.reflection.MetaObject;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import java.util.Date;@Configurationpublic class MybatisplusConfig implements MetaObjectHandler {    @Bean    public MybatisPlusInterceptor mybatisPlusInterceptor() {        MybatisPlusInterceptor interceptor = new MybatisPlusInterceptor();        PaginationInnerInterceptor paginationInnerInterceptor = new PaginationInnerInterceptor();        paginationInnerInterceptor.setOverflow(true);        interceptor.addInnerInterceptor(paginationInnerInterceptor);        return interceptor;    }    @Override    public void insertFill(MetaObject metaObject) {    }    @Override    public void updateFill(MetaObject metaObject) {        this.setFieldValByName("updateTime" , new Date() , metaObject);    }}